void randomMapGenerate() { // функция случаной генерации звёзд
	int randomElementX = 0;//случайный элемент по горизонтали
	int randomElementY = 0;//случ эл-т по вертикали
	srand(time(0)); //рандом по времени	
	int countStone = 100; //количество звезд
	while (countStone > 0) {
		randomElementX = 1 + rand() % (width - 1);//рандомное по иксу от 1 до ширина карты-1, чтобы не получать числа бордюра карты
		randomElementY = 1 + rand() % (hight - 1);//по игреку так же
		if (Map[randomElementY][randomElementX] == ' ') {//если встретили символ пробел, 
			Map[randomElementY][randomElementX] = '1'; //то ставим туда звезду.
			
			countStone--;
		}
	}
}
